#!/usr/bin/expect
# 設定 timeout 時間，以秒為單位。 set timeout 20
# 啟動 telnet spawn telnet -8 bbs.kkcity.com.tw
# 將 my_id 與 my_password 分別置換成您的帳號與密碼。 set ID 帳號 set PASSWD 密碼
# 當 telnet 輸出 guest，便輸入帳號與密碼，超過時限則直接離開。 expect "guest" { send "$ID\r$PASSWD\r" } timeout { exit }
#把畫面轉交給使用者控制，並每隔三百秒送出 " " interact timeout 300 { send " " } exit

ts = new Date().getTime();
p = (ts - ss) / 1000;
p = (p>1)1:p;
target.style.top = sp + (ep - sp) * p + "px";
if (p == 1)
{
	clearInterval(Interval);
}

function hlGo (type)
{
	if (!ts && !(type ^ hlFlag))
	{
		(type)?(tS.display = "block",delEvent(tA, "mouseover", hlMouseIn)):delEvent(body, "mousemove", hlMouseMove);
		ss = ts = new Date().getTime();
		this.Interval = setInterval("hlMove("+type+");", 20);
	}
}

function hlMove (type)
{
	ts = new Date().getTime();
	p = (ts-ss)/period;
	p = (p>1)?1:p;
	p = (type)?p:1-p;
	tS.top = iP+Math.sin(p*Math.PI/2)*tH+"px";
	if (isO) tS.opacity = p*0.96;
	if (!(p - type))
	{
		if (type && autofocus) tList.firstChild.firstChild.focus();
		ts = 0;
		clearInterval(this.Interval);
		(type)?addEvent(body, "mousemove", hlMouseMove):(tS.display = "none",addEvent(tA, "mouseover", hlMouseIn));
	}
}

