function* idMaker(){
    var index = 0;
    while(true)
        yield index++;
}

var myIterable = {}
myIterable["@@iterator"] = () => (function*(){
    yield 1;
    yield 2;
    yield 3
})();

for (let value of myIterable) {
    console.log(value);
} // 1, 2, 3
